let dict = "test"
function chords(pattern, chrd) {
  chrd = chrd || chord
  return n(pattern).mode(mode).chord(chrd).dict(dict).voicing()
}

function bd(pattern, intro = false) {
  return stack(
    s(pattern).bank("tr909").gain(intro ? 0.35 : 0.55).shape(0.2)
      .clip(.7)
      .hpf(140)
      .compressor("-17:20:10:0.002:0.03"),
    s(pattern).bank("tr909").gain(0.02).shape(0.5)
      .clip(.7)
      .hpf(2000)
      .compressor("-17:20:10:0.002:0.03")
  )
}

function hh(pattern) {
  return s(pattern).bank("tr808")
    .legato(0.2)
    .gain(1.7)
    .hpf(6600)
}

function oh(pattern) {
  return s(pattern).bank("tr909").legato(0.25).gain(0.5).hpf(5000)
}

function backing(add, gain){
  return chords("<0,1,3,4>".layer(x => x.add(add)).struct("<[x - - x] [- - x -]>*4")
    .gain(gain).clip(.4), chord2)
  .room(.05)
  .delay(.2)
  .pan(choose("0.3","0.4","0.6","0.7"))
  .shape(.3)
  .hpf(600)
}

function noise() {
